const express=require("express");const{spawn}=require("child_process");const path=require("path");const app=express();const port=3e3;app.use(express.static("public"));app.get("/",(req,d=res)=>{res.sendFile(path.join(__dirname,"public","index.html"))});app.get("/app",(req,res)=>{let largeDataSet=[];const python=spawn("python",["script2.py",JSON.stringify(req.query)]);python.stdout.on("data",function(data){console.log("Pipe data from python script ...");largeDataSet.push(data)});python.on("close",code=>{console.log(`child process close all stdio with code ${code}`);res.send(largeDataSet.join(""))})});app.listen(port,()=>{console.log(`App listening on port ${port}!`)});